package com.demoslideupdown.expandablelist_anim.activity;import android.os.Bundle;import android.support.constraint.ConstraintLayout;import android.support.v7.app.AppCompatActivity;import android.support.v7.widget.AppCompatImageView;import android.support.v7.widget.DividerItemDecoration;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import com.demoslideupdown.expandablelist_anim.model.Beer;import com.demoslideupdown.expandablelist_anim.adapter.BeerAdapter;import com.demoslideupdown.expandablelist_anim.model.Drink;import com.demoslideupdown.expandablelist_anim.adapter.LiquorAdapter;import com.demoslideupdown.R;import java.util.ArrayList;import java.util.List;import butterknife.BindView;import butterknife.ButterKnife;import butterknife.OnClick;import static com.demoslideupdown.utils.AnimUtils.animateViewToHide;import static com.demoslideupdown.utils.AnimUtils.animateViewToShow;import static com.demoslideupdown.utils.AnimUtils.slideDown;import static com.demoslideupdown.utils.AnimUtils.slideUp;public class ExpandableListAnimated extends AppCompatActivity{    @BindView(R.id.rvBeerList)    RecyclerView rvBeerList;    @BindView(R.id.clLiquor)    ConstraintLayout clLiquor;    @BindView(R.id.clBeer)    ConstraintLayout clBeer;    @BindView(R.id.ivBeer)    AppCompatImageView ivBeer;    @BindView(R.id.ivLiquor)    AppCompatImageView ivLiquor;    @BindView(R.id.rvLiquorList)    RecyclerView rvLiquorList;    private boolean beerBoolean=true,liquorBoolean=true;    @Override    protected void onCreate (Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_expandable_list_animated);        ButterKnife.bind(this);        rvBeerList.setHasFixedSize(false);        rvBeerList.addItemDecoration(new DividerItemDecoration(getApplicationContext(),                DividerItemDecoration.VERTICAL));        rvBeerList.setLayoutManager(new LinearLayoutManager(getApplicationContext()));        populateBeer();        rvLiquorList.setHasFixedSize(false);        rvLiquorList.addItemDecoration(new DividerItemDecoration(getApplicationContext(),                DividerItemDecoration.VERTICAL));        rvLiquorList.setLayoutManager(new LinearLayoutManager(getApplicationContext()));        populateLiquor();    }    private void populateLiquor() {        List<Beer> beers=new ArrayList<>();                   Drink drink=new Drink(0,"Gin");                   Drink drink2=new Drink(0,"Bourbon");                   Drink drink3=new Drink(0,"Rum");                   Drink drink4=new Drink(0,"Vodka");                   Drink drink5=new Drink(0,"Whisky");                   Drink drink6=new Drink(0,"Wine");                   List<Drink> drinks=new ArrayList<>();                   drinks.add(drink);                   drinks.add(drink2);        List<Drink> drinks2=new ArrayList<>();        drinks2.add(drink3);        drinks2.add(drink4);        List<Drink> drinks3=new ArrayList<>();        drinks3.add(drink5);        drinks3.add(drink6);        List<Drink> drinks4=new ArrayList<>();        beers.add(new Beer(0,"Well Drinks",drinks));        beers.add(new Beer(1,"Cocktail",drinks2));        beers.add(new Beer(2,"House Specialities",drinks3));        beers.add(new Beer(3,"Top Shelf",null));        LiquorAdapter liquorAdapter= new  LiquorAdapter(getApplicationContext(),beers);        rvLiquorList.setAdapter(liquorAdapter);    }    private void populateBeer() {        List<Beer> beers=new ArrayList<>();        beers.add(new Beer(0,"Domestic",null));        beers.add(new Beer(1,"Imported",null));        beers.add(new Beer(2,"Cider",null));        beers.add(new Beer(3,"Craft Beer",null));        BeerAdapter beerAdapter= new  BeerAdapter(getApplicationContext(),beers);        rvBeerList.setAdapter(beerAdapter);    }    @OnClick(R.id.clBeer)    void onCheckBeer(){        if(beerBoolean){            ivBeer.animate().rotation(-180).start();            slideDown(clLiquor,rvBeerList);            beerBoolean=false;        } else{            ivBeer.animate().rotation(0).start();            slideUp(clLiquor,rvBeerList);            beerBoolean=true;        }    }    @OnClick(R.id.clLiquor)    void onCheckLiquor(){        if(liquorBoolean){            ivLiquor.animate().rotation(-180).start();            animateViewToShow(rvLiquorList);            liquorBoolean=false;        } else{            ivLiquor.animate().rotation(0).start();            animateViewToHide(rvLiquorList);            liquorBoolean=true;        }    }}