package com.demoslideupdown.shimmer.adapter;import android.support.annotation.NonNull;import android.support.v7.widget.AppCompatImageView;import android.support.v7.widget.AppCompatTextView;import android.support.v7.widget.RecyclerView;import android.util.Log;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import com.bumptech.glide.Glide;import com.demoslideupdown.R;import com.demoslideupdown.shimmer.model.ItemCard;import java.util.ArrayList;import java.util.List;import butterknife.BindView;import butterknife.ButterKnife;public class CardAdapter extends RecyclerView.Adapter<CardAdapter.ItemHolder> {    private List<ItemCard> mCards = new ArrayList<>();    private static final String  TAG=CardAdapter.class.getSimpleName();    @NonNull    @Override    public ItemHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View view = LayoutInflater.from(parent.getContext()).inflate(R.layout.layout_news_card,                parent, false);        return new ItemHolder(view);    }    @Override    public void onBindViewHolder(@NonNull ItemHolder holder, int position) {        holder.bind(mCards.get(position));    }    @Override    public int getItemCount() {        return mCards.size();    }    public void setCards(List<ItemCard> cards) {        if (cards == null) {            return;        }        Log.d(TAG, "setCards: "+cards.size());        mCards = cards;    }    public class ItemHolder extends RecyclerView.ViewHolder {        @BindView(R.id.cardTitle)         AppCompatTextView cardTitle;        @BindView(R.id.cardSubtitle)         AppCompatTextView cardSubtitle;        @BindView(R.id.cardSummary)         AppCompatTextView cardSummary;        @BindView(R.id.cardImage)         AppCompatImageView cardImage;        private ItemHolder(View itemView) {            super(itemView);            ButterKnife.bind(this,itemView);        }        public void bind(ItemCard card) {            cardTitle.setText(card.getTitle());            cardSubtitle.setText(card.getDescription());            cardSummary.setText(card.getSummaryText());           Glide.with(itemView.getContext()).load(card.getThumbnailUrl()).into(cardImage);        }    }}