package com.demoslideupdown.utils;import android.content.res.Resources;import android.support.annotation.StringRes;import com.demoslideupdown.shimmer.model.ItemCard;import com.demoslideupdown.R;import java.util.Arrays;import java.util.List;public class DataUtils {    public static List<ItemCard> getListCards(Resources resources) {        ItemCard ndtvCard = createItemCard(resources, R.string.ndtv_titletext, R.string.ndtv_image_url,                R.string.ndtv_subtext, R.string.ndtv_summarytext);        ItemCard opCard = createItemCard(resources, R.string.op_titletext, R.string.op_image_url,                R.string.op_subtext, R.string.op_summarytext);        ItemCard gotCard = createItemCard(resources, R.string.got_titletext, R.string.got_image_url,                R.string.got_subtext, R.string.got_summarytext);        ItemCard jetCard = createItemCard(resources, R.string.jet_titletext, R.string.jet_image_url,                R.string.jet_subtext, R.string.jet_summarytext);        ItemCard noteCard = createItemCard(resources, R.string.note5_titletext, R.string.note5_image_url,                R.string.note5_subtext, R.string.note5_summarytext);        ItemCard on7Card = createItemCard(resources, R.string.on7_titletext, R.string.on7_image_url,                R.string.on7_subtext, R.string.on7_summarytext);        return Arrays.asList(ndtvCard, opCard, gotCard, jetCard,noteCard,on7Card);    }    private static ItemCard createItemCard(Resources resources, @StringRes int title, @StringRes int imageUrl,                                           @StringRes int description, @StringRes int summary) {        ItemCard itemCard = new ItemCard();        itemCard.setTitle(resources.getString(title));        itemCard.setThumbnailUrl(resources.getString(imageUrl));        itemCard.setDescription(resources.getString(description));        itemCard.setSummaryText(resources.getString(summary));        return itemCard;    }}